CREATE TABLE product(
   id                     INTEGER  NOT NULL PRIMARY KEY 
  ,kategori               VARCHAR(255)
  ,nama                   VARCHAR(255)
  ,deskripsi              VARCHAR(255)
  ,berat                  INTEGER 
  ,harga                  INTEGER 
  ,gambar                 VARCHAR(255)
  ,stock                  INTEGER 
  ,warna                  VARCHAR(255)
  ,ukuran                 VARCHAR(255)
  ,jenis_bahan            VARCHAR(255)
  ,motif                  VARCHAR(255)
  ,penutup                VARCHAR(255)
);
